class Solution {
public:

	int smallestDistancePair(vector<int>& nums, int k) {
		sort(begin(nums), end(nums)); // n*log(n)
		const size_t n = nums.size();

		// dist 0..max-min
		int lo = 0, hi = nums[n - 1] - nums[0]; // max - min

		// search in [lo, hi] for L: countPairs with dist <= k
		while (lo < hi) { // log M
			int mi = lo + (hi - lo) / 2;
			size_t count = 0, left = 0;
			for (size_t j = 0; j < n; ++j) { // sum counts over all j - O(n)
				while (nums[j] - nums[left] > mi)
					++left; // decreasing max dist
				count += j - left; // (j - left) pairs (i1, j) : dist <= mi
			}
			if (count < k)
				lo = mi + 1;
			else
				hi = mi;
		}
		return lo;
	}
};

