class ScopedTimer {
	string name;
	uint64_t cycles0;
	std::chrono::steady_clock::time_point time0;
public:

	explicit ScopedTimer(const string& name) : name(name), cycles0(rdtsc()), time0(std::chrono::steady_clock::now()) {

	}

	~ScopedTimer() {
		auto cycles1 = rdtsc(); 
		auto time1 = std::chrono::steady_clock::now();
		
		cout 
			<< "TIME " << setw(10) << name << ": "
			<< "CPU Cycles: " << setw(14) << (cycles1 - cycles0)
			<< ", time elapsed: " << setw(8) << std::chrono::duration_cast<std::chrono::milliseconds>(time1 - time0).count() << "ms" << endl;
	}
};

