
#include <iostream>
#include <iomanip>
#include <string>
#include <vector>
#include <unordered_map>
#include <unordered_set>

using namespace std;

/*
https://leetcode.com/problems/longest-duplicate-substring/
https://leetcode.com/explore/challenge/card/june-leetcoding-challenge/541/week-3-june-15th-june-21st/3365/

Given a string S, consider all duplicated substrings: (contiguous) substrings of S that occur 2 or more times.  (The occurrences may overlap.)

Return any duplicated substring that has the longest possible length.  (If S does not have a duplicated substring, the answer is "".)



Example 1:

Input: "banana"
Output: "ana"
Example 2:

Input: "abcd"
Output: ""


Note:

2 <= S.length <= 10^5
S consists of lowercase English letters.

Binary search for the length of the answer. (If there's an answer of length 10, then there are answers of length 9, 8, 7, ...)
To check whether an answer of length K exists, we can use Rabin-Karp 's algorithm.

*/

//Runtime: 1520 ms, faster than 70.10% of C++ online submissions for Longest Duplicate Substring.
//Memory Usage : 238.8 MB, less than 47.24% of C++ online submissions for Longest Duplicate Substring.
class Solution {
	
public:

	typedef unsigned long long hash_t;
	unsigned long long x, mod;

	Solution() : x(17), mod(1ull << 61) {

	}

	hash_t calcHash(const string& t) {
		//return hash<string>().operator()(t);
		hash_t result = 0;
		for (const auto c : t) {
			result *= x;
			result %= mod;
			result += c;
			result %= mod;
		}
		return result;
	}

	hash_t recalcHash(hash_t h, const hash_t XM1, const char removed, const char appended) {
		h -= removed * XM1;
		h %= mod;
		h *= x;
		h %= mod;
		h += appended;
		h %= mod;
		return h;
	}

	string findDupSubstring(const string& S, const int m) {
		unordered_map<hash_t, int> seen;
		const size_t n = S.length();

		hash_t XM1 = 1;
		for (int i = 1; i <= m - 1; ++i)
			XM1 *= x;

		string t = S.substr(0, m);
		hash_t h = calcHash(t);
		seen.emplace(h, 0);
		for (int i1 = 1, i2 = m; i2 < n; ++i1, ++i2) {
			//t = t.substr(1);
			//t += S[i2];
			//const size_t h = calcHash(t);
			h = recalcHash(h, XM1, S[i1-1], S[i2]);
			auto it = seen.find(h);
			if (it != end(seen) && S.substr(it->second, m) == S.substr(i1, m))
				return S.substr(i1, m);
			seen.emplace_hint(it, h, i1);
		}
		return "";
	}

	string longestDupSubstring(string S) {
		const size_t n = S.length();
		int l = 1, r = n;
		string result;
		while (r - l > 1) {
			int m = l + (r - l) / 2;
			string substr = findDupSubstring(S, m);
			if (substr.empty())
				r = m;
			else {
				l = m;
				result = substr;
			}
		}
		return result;
	}
};

int main()
{
	cout << Solution().calcHash("abc") << endl;
	cout << Solution().calcHash("bcd") << endl;
	cout << Solution().recalcHash(Solution().calcHash("abc"), 
		Solution().x * Solution().x, 'a', 'd') << endl;

	vector<pair<string, string>> tests = {
		{ string("") +
		  string(""), 
		"bbqyr" },
		{ "banana", "ana" },
		{ "abcd", "" }
	};

	for (const auto& t : tests) {
		auto r = Solution().longestDupSubstring(t.first);
		cout << boolalpha << (r == t.second) << ": " << t.first << " -> " << r << " (" << t.second << ")" << endl;
	}

    return 0;
}

